import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import CreateMentor from './pages/CreateMentor';
import axios from 'axios';

import './styles/App.css';

function App() {
  const [step, setStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [careerRoadmap, setCareerRoadmap] = useState(null);
  const [selectedCareer, setSelectedCareer] = useState('');
  const [selectedTechnology, setSelectedTechnology] = useState('');
  const [userId, setUserId] = useState(null);

  const careerPaths = {
    'software-developer': {
      title: 'Software Developer',
      technologies: ['Frontend', 'Backend', 'Full Stack', 'Mobile Development', 'DevOps']
    },
    'data-scientist': {
      title: 'Data Scientist',
      technologies: ['Machine Learning', 'Data Analytics', 'Big Data', 'AI Engineering']
    },
    'ui-ux-designer': {
      title: 'UI/UX Designer',
      technologies: ['UI Design', 'UX Research', 'Product Design', 'Interaction Design']
    },
    'cloud-architect': {
      title: 'Cloud Architect',
      technologies: ['AWS', 'Azure', 'Google Cloud', 'Multi-cloud']
    },
    'cybersecurity-analyst': {
      title: 'Cybersecurity Analyst',
      technologies: ['Network Security', 'Application Security', 'Cloud Security', 'Ethical Hacking']
    }
  };

  const careerTemplates = {
    'software-developer': {
      title: 'Software Developer Career Path',
      skills: [
        'Programming fundamentals (JavaScript, Python, etc.)',
        'Web development (HTML, CSS, React)',
        'Version control (Git)',
        'Database management (SQL, MongoDB)',
        'Problem-solving and algorithms',
        'Software architecture patterns'
      ],
      timeline: [
        '0-6 months: Master programming basics and web fundamentals',
        '6-12 months: Build portfolio projects and learn frameworks',
        '1-2 years: Secure internship or junior developer position',
        '2-3 years: Advance to mid-level developer',
        '3-5 years: Senior developer opportunities'
      ],
      resources: [
        'freeCodeCamp - https://www.freecodecamp.org',
        'The Odin Project - https://www.theodinproject.com',
        'MDN Web Docs - https://developer.mozilla.org',
        'LeetCode for practice - https://leetcode.com',
        'GitHub for project hosting'
      ],
      certifications: [
        'AWS Certified Developer',
        'MongoDB Certified Developer',
        'Microsoft Certified: Azure Developer',
        'Google Cloud Certified Developer'
      ]
    },
    'data-scientist': {
      title: 'Data Scientist Career Path',
      skills: [
        'Python programming',
        'Statistics and probability',
        'Machine learning algorithms',
        'Data visualization',
        'SQL and database concepts',
        'Big data technologies'
      ],
      timeline: [
        '0-6 months: Learn Python and statistics basics',
        '6-12 months: Master ML algorithms and data visualization',
        '1-2 years: Build portfolio projects and get certifications',
        '2-3 years: Junior data scientist position',
        '3-5 years: Senior data scientist role'
      ],
      resources: [
        'Kaggle - https://www.kaggle.com',
        'DataCamp - https://www.datacamp.com',
        'Coursera - Machine Learning Specialization',
        'Fast.ai - https://www.fast.ai',
        'GitHub for project hosting'
      ],
      certifications: [
        'IBM Data Science Professional Certificate',
        'Google Data Analytics Professional Certificate',
        'Azure Data Scientist Associate',
        'TensorFlow Developer Certificate'
      ]
    },
    'ui-ux-designer': {
      title: 'UI/UX Designer Career Path',
      skills: [
        'User Interface Design',
        'User Experience Principles',
        'Wireframing and Prototyping',
        'Design Tools (Figma, Adobe XD)',
        'User Research Methods',
        'Visual Design Principles'
      ],
      timeline: [
        '0-6 months: Learn design fundamentals and tools',
        '6-12 months: Build portfolio and practice projects',
        '1-2 years: Junior UI/UX designer position',
        '2-3 years: Mid-level designer role',
        '3-5 years: Senior designer opportunities'
      ],
      resources: [
        'Figma tutorials - https://www.figma.com/resources',
        'Google UX Design Certificate on Coursera',
        'Dribbble for inspiration - https://dribbble.com',
        'UX Design Institute - https://www.uxdesigninstitute.com',
        'Behance for portfolio - https://www.behance.net'
      ],
      certifications: [
        'Google UX Design Certificate',
        'Certified User Experience Analyst (CXA)',
        'Professional Certificate in UI Design',
        'Adobe XD Certification'
      ]
    }
  };

  const generateCareerPlan = async () => {
    try {
      setIsLoading(true);
      
      // Simulate API call delay for better UX
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const roadmap = careerTemplates[selectedCareer] || careerTemplates['software-developer'];
      setCareerRoadmap(roadmap);
      
    } catch (error) {
      console.error('Error generating career plan:', error);
      alert('Failed to generate career plan. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const saveRoadmap = async () => {
    try {
      if (!userId) return;
      
      const response = await axios.post('/api/roadmaps', {
        userId,
        careerPath: selectedCareer,
        technology: selectedTechnology,
        roadmap: careerRoadmap
      });

      if (response.data.success) {
        alert('Roadmap saved successfully!');
      }
    } catch (error) {
      console.error('Error saving roadmap:', error);
      alert('Failed to save roadmap. Please try again.');
    }
  };

  const handleDownload = () => {
    const element = document.createElement('a');
    const fileContent = JSON.stringify(careerRoadmap, null, 2);
    const file = new Blob([fileContent], {type: 'text/plain'});
    element.href = URL.createObjectURL(file);
    element.download = `career-roadmap-${selectedCareer}.txt`;
    document.body.appendChild(element);
    element.click();
  };

  return (
    <Router>
      <div className="app">
        <NavBar />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/create-mentor" element={<CreateMentor />} />
          </Routes>
          
          <div className="career-planner">
            {step === 1 && (
              <div className="question-section">
                <h2>Let's start with your career goal</h2>
                <select 
                  value={selectedCareer}
                  onChange={(e) => {
                    setSelectedCareer(e.target.value);
                    setSelectedTechnology('');
                  }}
                  className="career-select"
                >
                  <option value="">Select your career path</option>
                  {Object.entries(careerPaths).map(([key, value]) => (
                    <option key={key} value={key}>{value.title}</option>
                  ))}
                </select>
                {selectedCareer && (
                  <button onClick={() => setStep(2)} className="next-button">
                    Next
                  </button>
                )}
              </div>
            )}

            {step === 2 && (
              <div className="question-section">
                <h2>Choose your technology path</h2>
                <select 
                  value={selectedTechnology}
                  onChange={(e) => setSelectedTechnology(e.target.value)}
                  className="tech-select"
                >
                  <option value="">Select technology focus</option>
                  {careerPaths[selectedCareer]?.technologies.map((tech) => (
                    <option key={tech} value={tech}>{tech}</option>
                  ))}
                </select>
                <div className="button-group">
                  <button onClick={() => setStep(1)} className="back-button">
                    Back
                  </button>
                  {selectedTechnology && (
                    <button onClick={generateCareerPlan} className="create-plan-button">
                      {isLoading ? 'Generating Plan...' : 'Create Career Plan'}
                    </button>
                  )}
                </div>
              </div>
            )}

            {careerRoadmap && (
              <div className="roadmap-container">
                <h2>{careerRoadmap.title}</h2>
                
                <section className="roadmap-section">
                  <h3>Required Skills</h3>
                  <ul>
                    {careerRoadmap.skills.map((skill, index) => (
                      <li key={index}>{skill}</li>
                    ))}
                  </ul>
                </section>

                <section className="roadmap-section">
                  <h3>Timeline & Milestones</h3>
                  <ul>
                    {careerRoadmap.timeline.map((milestone, index) => (
                      <li key={index}>{milestone}</li>
                    ))}
                  </ul>
                </section>

                <section className="roadmap-section">
                  <h3>Learning Resources</h3>
                  <ul>
                    {careerRoadmap.resources.map((resource, index) => (
                      <li key={index}>{resource}</li>
                    ))}
                  </ul>
                </section>

                <section className="roadmap-section">
                  <h3>Recommended Certifications</h3>
                  <ul>
                    {careerRoadmap.certifications.map((cert, index) => (
                      <li key={index}>{cert}</li>
                    ))}
                  </ul>
                </section>

                <div className="roadmap-actions">
                  <button onClick={handleDownload} className="download-button">
                    Download Roadmap
                  </button>
                  <button onClick={saveRoadmap} className="save-button">
                    Save to Profile
                  </button>
                </div>
              </div>
            )}
          </div>
        </main>
        <footer className="footer">
          <p>&copy; 2024 Your Project. All rights reserved.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
