import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import CreateMentor from './pages/CreateMentor';
import axios from 'axios';

import './styles/App.css';

function App() {
  const [isLoading, setIsLoading] = React.useState(false);
  const [careerRoadmap, setCareerRoadmap] = React.useState(null);
  const [selectedCareer, setSelectedCareer] = React.useState('technology');

  const generateCareerPlan = async () => {
    try {
      setIsLoading(true);
      
      const careerTemplate = {
        'software-developer': {
          skills: [
            'Programming fundamentals',
            'Web development basics',
            'Version control (Git)',
            'Database management'
          ],
          timeline: [
            '0-6 months: Learn programming basics',
            '6-12 months: Build projects',
            '1-2 years: Internship/Junior role',
            '2+ years: Mid-level developer'
          ],
          resources: [
            'freeCodeCamp',
            'The Odin Project',
            'MDN Web Docs',
            'GitHub'
          ]
        },
        // Add more career templates...
      };

      const roadmap = careerTemplate[selectedCareer] || careerTemplate['software-developer'];
      setCareerRoadmap(JSON.stringify(roadmap, null, 2));
      
    } catch (error) {
      console.error('Error generating career plan:', error);
      alert('Failed to generate career plan. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Router>
      <div className="app">
        <NavBar />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/create-mentor" element={<CreateMentor />} />
          </Routes>
        </main>
        <footer className="footer">
          <p>&copy; 2024 Your Project. All rights reserved.</p>
        </footer>
        <button 
          onClick={generateCareerPlan}
          disabled={isLoading}
          className="create-plan-button"
        >
          {isLoading ? 'Generating Plan...' : 'Create Plan'}
        </button>

        {careerRoadmap && (
          <div className="roadmap-container">
            <h2>Your Career Roadmap</h2>
            <pre className="roadmap-content">
              {careerRoadmap}
            </pre>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
