import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CreateMentor = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    careerGoal: '',
    technology: '',
    timeline: '',
    planPreference: '',
    userId: null
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Check if user is logged in
  useEffect(() => {
    const checkAuth = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/check-auth');
        console.log(response);
        if (response.data.user) {
          setFormData(prev => ({ ...prev, userId: response.data.user.id }));
        } else {
          navigate('/login', { state: { from: '/create-mentor' } });
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        navigate('/login', { state: { from: '/create-mentor' } });
      }
    };
    checkAuth();
  }, [navigate]);

  const handleSubmit = async () => {
    try {
      setIsLoading(true);
      setError(null);

      // Validate form data
      if (!formData.careerGoal || !formData.technology || !formData.timeline || !formData.planPreference) {
        throw new Error('Please fill in all required fields');
      }

      console.log('Submitting form data:', formData);

      const response = await axios.post(
        'http://localhost:5000/api/mentor/create-plan',
        formData,
        { 
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          },
          timeout: 60000 // Increased timeout to 60 seconds
        }
      );

      console.log('API Response:', response.data);

      if (response.data.success && response.data.plan) {
        navigate('/roadmap');
      } else {
        throw new Error('Failed to create plan - no plan data received');
      }
    } catch (error) {
      console.error('Error creating plan:', error);
      
      // More detailed error handling
      let errorMessage = 'Failed to create learning plan. Please try again.';
      
      if (error.response) {
        console.error('Error Response:', error.response.data);
        console.error('Error Status:', error.response.status);
        errorMessage = error.response.data.message || errorMessage;
      } else if (error.request) {
        console.error('No response received:', error.request);
        errorMessage = 'No response received from server. Please check your connection.';
      } else {
        console.error('Error:', error.message);
        errorMessage = error.message;
      }
      
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const questions = [
    {
      step: 1,
      title: "Let's start with your career goal",
      question: "What is your career goal?",
      placeholder: "e.g., Full Stack Developer, Data Scientist, Cloud Engineer",
      field: "careerGoal"
    },
    {
      step: 2,
      title: "Choose your technology path",
      question: "Which technology do you want to learn?",
      placeholder: "e.g., MERN Stack, Python with ML, AWS Cloud",
      field: "technology"
    },
    {
      step: 3,
      title: "Set your learning timeline",
      question: "How long do you want to spend learning?",
      type: "select",
      options: ["3 months", "6 months", "1 year"],
      field: "timeline"
    },
    {
      step: 4,
      title: "Choose your learning schedule",
      question: "How would you like to plan your learning?",
      type: "select",
      options: ["Daily basis", "Weekly basis", "Monthly basis"],
      field: "planPreference"
    }
  ];

  const currentQuestion = questions.find(q => q.step === currentStep);

  const handleNext = () => {
    if (currentStep < questions.length) {
      setCurrentStep(currentStep + 1);
    } else {
      handleSubmit();
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  return (
    <div className="create-mentor-container">
      <div className="questionnaire-card">
        <div className="progress-bar">
          {questions.map((q, index) => (
            <div 
              key={q.step}
              className={`progress-step ${currentStep >= q.step ? 'active' : ''}`}
            >
              {index + 1}
            </div>
          ))}
        </div>

        <h2>{currentQuestion.title}</h2>
        <p className="question-text">{currentQuestion.question}</p>

        {currentQuestion.type === 'select' ? (
          <div className="options-grid">
            {currentQuestion.options.map((option) => (
              <button
                key={option}
                className={`option-button ${formData[currentQuestion.field] === option ? 'selected' : ''}`}
                onClick={() => setFormData({ ...formData, [currentQuestion.field]: option })}
              >
                {option}
              </button>
            ))}
          </div>
        ) : (
          <input
            type="text"
            className="question-input"
            placeholder={currentQuestion.placeholder}
            value={formData[currentQuestion.field]}
            onChange={(e) => setFormData({ 
              ...formData, 
              [currentQuestion.field]: e.target.value 
            })}
          />
        )}

        <div className="button-group">
          {currentStep > 1 && (
            <button 
              className="nav-button previous"
              onClick={handlePrevious}
            >
              <span className="material-icons">arrow_back</span>
              Previous
            </button>
          )}
          <button
            className="nav-button next"
            onClick={handleNext}
            disabled={!formData[currentQuestion.field]}
          >
            {currentStep === questions.length ? 'Create Plan' : 'Next'}
            {currentStep !== questions.length && (
              <span className="material-icons">arrow_forward</span>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateMentor;
