import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import RoadmapWizard from './components/RoadmapWizard';
import Profile from './pages/Profile';
import { Link } from 'react-router-dom';

import axios from 'axios';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

import './styles/App.css';
import { GoogleOAuthProvider } from '@react-oauth/google';

function App() {
  const [step, setStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [careerRoadmap, setCareerRoadmap] = useState(null);
  const [selectedCareer, setSelectedCareer] = useState('');
  const [selectedTechnology, setSelectedTechnology] = useState('');
  const [userId, setUserId] = useState(null);

  const careerPaths = {
    'software-developer': {
      title: 'Software Developer',
      technologies: ['Frontend', 'Backend', 'Full Stack', 'Mobile Development', 'DevOps']
    },
    'data-scientist': {
      title: 'Data Scientist',
      technologies: ['Machine Learning', 'Data Analytics', 'Big Data', 'AI Engineering']
    },
    'ui-ux-designer': {
      title: 'UI/UX Designer',
      technologies: ['UI Design', 'UX Research', 'Product Design', 'Interaction Design']
    },
    'cloud-architect': {
      title: 'Cloud Architect',
      technologies: ['AWS', 'Azure', 'Google Cloud', 'Multi-cloud']
    },
    'cybersecurity-analyst': {
      title: 'Cybersecurity Analyst',
      technologies: ['Network Security', 'Application Security', 'Cloud Security', 'Ethical Hacking']
    }
  };

  const careerTemplates = {
    'software-developer': {
      title: 'Software Developer Career Path',
      skills: [
        'Programming fundamentals (JavaScript, Python, etc.)',
        'Web development (HTML, CSS, React)',
        'Version control (Git)',
        'Database management (SQL, MongoDB)',
        'Problem-solving and algorithms',
        'Software architecture patterns'
      ],
      timeline: [
        '0-6 months: Master programming basics and web fundamentals',
        '6-12 months: Build portfolio projects and learn frameworks',
        '1-2 years: Secure internship or junior developer position',
        '2-3 years: Advance to mid-level developer',
        '3-5 years: Senior developer opportunities'
      ],
      resources: [
        'freeCodeCamp - https://www.freecodecamp.org',
        'The Odin Project - https://www.theodinproject.com',
        'MDN Web Docs - https://developer.mozilla.org',
        'LeetCode for practice - https://leetcode.com',
        'GitHub for project hosting'
      ],
      certifications: [
        'AWS Certified Developer',
        'MongoDB Certified Developer',
        'Microsoft Certified: Azure Developer',
        'Google Cloud Certified Developer'
      ]
    },
    'data-scientist': {
      title: 'Data Scientist Career Path',
      skills: [
        'Python programming',
        'Statistics and probability',
        'Machine learning algorithms',
        'Data visualization',
        'SQL and database concepts',
        'Big data technologies'
      ],
      timeline: [
        '0-6 months: Learn Python and statistics basics',
        '6-12 months: Master ML algorithms and data visualization',
        '1-2 years: Build portfolio projects and get certifications',
        '2-3 years: Junior data scientist position',
        '3-5 years: Senior data scientist role'
      ],
      resources: [
        'Kaggle - https://www.kaggle.com',
        'DataCamp - https://www.datacamp.com',
        'Coursera - Machine Learning Specialization',
        'Fast.ai - https://www.fast.ai',
        'GitHub for project hosting'
      ],
      certifications: [
        'IBM Data Science Professional Certificate',
        'Google Data Analytics Professional Certificate',
        'Azure Data Scientist Associate',
        'TensorFlow Developer Certificate'
      ]
    },
    'ui-ux-designer': {
      title: 'UI/UX Designer Career Path',
      skills: [
        'User Interface Design',
        'User Experience Principles',
        'Wireframing and Prototyping',
        'Design Tools (Figma, Adobe XD)',
        'User Research Methods',
        'Visual Design Principles'
      ],
      timeline: [
        '0-6 months: Learn design fundamentals and tools',
        '6-12 months: Build portfolio and practice projects',
        '1-2 years: Junior UI/UX designer position',
        '2-3 years: Mid-level designer role',
        '3-5 years: Senior designer opportunities'
      ],
      resources: [
        'Figma tutorials - https://www.figma.com/resources',
        'Google UX Design Certificate on Coursera',
        'Dribbble for inspiration - https://dribbble.com',
        'UX Design Institute - https://www.uxdesigninstitute.com',
        'Behance for portfolio - https://www.behance.net'
      ],
      certifications: [
        'Google UX Design Certificate',
        'Certified User Experience Analyst (CXA)',
        'Professional Certificate in UI Design',
        'Adobe XD Certification'
      ]
    }
  };

  const generateCareerPlan = async () => {
    try {
      setIsLoading(true);
      
      // Simulate API call delay for better UX
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      const roadmap = careerTemplates[selectedCareer] || careerTemplates['software-developer'];
      setCareerRoadmap(roadmap);
      
    } catch (error) {
      console.error('Error generating career plan:', error);
      alert('Failed to generate career plan. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const saveRoadmap = async () => {
    try {
      if (!userId) return;
      
      const response = await axios.post('/api/roadmaps', {
        userId,
        careerPath: selectedCareer,
        technology: selectedTechnology,
        roadmap: careerRoadmap
      });

      if (response.data.success) {
        alert('Roadmap saved successfully!');
      }
    } catch (error) {
      console.error('Error saving roadmap:', error);
      alert('Failed to save roadmap. Please try again.');
    }
  };

  const handleDownload = () => {
    const pdf = new jsPDF();
    
    // Add title
    pdf.setFontSize(20);
    pdf.text(careerRoadmap.title, 20, 20);
    
    // Add Skills section
    pdf.setFontSize(16);
    pdf.text('Required Skills', 20, 40);
    pdf.setFontSize(12);
    careerRoadmap.skills.forEach((skill, index) => {
      pdf.text(`• ${skill}`, 25, 50 + (index * 7));
    });
    
    // Add Timeline section
    let yPos = 50 + (careerRoadmap.skills.length * 7) + 10;
    pdf.setFontSize(16);
    pdf.text('Learning Timeline', 20, yPos);
    pdf.setFontSize(12);
    careerRoadmap.timeline.forEach((milestone, index) => {
      pdf.text(`• ${milestone}`, 25, yPos + 10 + (index * 7));
    });
    
    // Add Resources section
    yPos = yPos + (careerRoadmap.timeline.length * 7) + 20;
    pdf.setFontSize(16);
    pdf.text('Learning Resources', 20, yPos);
    pdf.setFontSize(12);
    careerRoadmap.resources.forEach((resource, index) => {
      pdf.text(`• ${resource}`, 25, yPos + 10 + (index * 7));
    });
    
    // Add Certifications section
    yPos = yPos + (careerRoadmap.resources.length * 7) + 20;
    pdf.setFontSize(16);
    pdf.text('Recommended Certifications', 20, yPos);
    pdf.setFontSize(12);
    careerRoadmap.certifications.forEach((cert, index) => {
      pdf.text(`• ${cert}`, 25, yPos + 10 + (index * 7));
    });

    // Save the PDF
    pdf.save(`career-roadmap-${selectedCareer}.pdf`);
  };

  return (
    <GoogleOAuthProvider clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}>
      <Router>
        <div className="app">
          <NavBar />
          <main className="main-content">
            <Routes>
              <Route path="/" element={
                <div>
                  <Home />
                  <div className="career-planner">
                    {!careerRoadmap && (
                      <RoadmapWizard
                        onComplete={async (preferences) => {
                          setSelectedCareer(preferences.careerGoal.toLowerCase().replace(/ /g, '-'));
                          setSelectedTechnology(preferences.technologies[0]);
                          await generateCareerPlan();
                        }}
                      />
                    )}

                    {careerRoadmap && (
                      <div className="roadmap-container">
                        <div className="roadmap-header">
                          <h2>{careerRoadmap.title}</h2>
                          <p className="roadmap-intro">Your personalized learning journey is ready! Follow this path to achieve your goals.</p>
                        </div>
                        
                        <div className="roadmap-grid">
                          <section className="roadmap-section skills-section">
                            <div className="section-header">
                              <span className="material-icons">psychology</span>
                              <h3>Required Skills</h3>
                            </div>
                            <div className="skills-grid">
                              {careerRoadmap.skills.map((skill, index) => (
                                <div key={index} className="skill-card">
                                  <span className="material-icons">check_circle</span>
                                  <p>{skill}</p>
                                </div>
                              ))}
                            </div>
                          </section>

                          <section className="roadmap-section timeline-section">
                            <div className="section-header">
                              <span className="material-icons">timeline</span>
                              <h3>Learning Timeline</h3>
                            </div>
                            <div className="timeline">
                              {careerRoadmap.timeline.map((milestone, index) => (
                                <div key={index} className="timeline-item">
                                  <div className="timeline-marker"></div>
                                  <div className="timeline-content">
                                    <p>{milestone}</p>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </section>

                          <section className="roadmap-section resources-section">
                            <div className="section-header">
                              <span className="material-icons">library_books</span>
                              <h3>Learning Resources</h3>
                            </div>
                            <div className="resources-grid">
                              {careerRoadmap.resources.map((resource, index) => {
                                const [name, url] = resource.split(' - ');
                                return (
                                  <a 
                                    key={index} 
                                    href={url} 
                                    target="_blank" 
                                    rel="noopener noreferrer" 
                                    className="resource-card"
                                  >
                                    <span className="material-icons">launch</span>
                                    <p>{name}</p>
                                  </a>
                                );
                              })}
                            </div>
                          </section>

                          <section className="roadmap-section certifications-section">
                            <div className="section-header">
                              <span className="material-icons">military_tech</span>
                              <h3>Recommended Certifications</h3>
                            </div>
                            <div className="certifications-grid">
                              {careerRoadmap.certifications.map((cert, index) => (
                                <div key={index} className="certification-card">
                                  <span className="material-icons">workspace_premium</span>
                                  <p>{cert}</p>
                                </div>
                              ))}
                            </div>
                          </section>
                        </div>

                        <div className="roadmap-actions">
                          <button onClick={handleDownload} className="action-button download-button">
                            <span className="material-icons">download</span>
                            Download Roadmap
                          </button>
                          <button onClick={saveRoadmap} className="action-button save-button">
                            <span className="material-icons">bookmark</span>
                            Save to Profile
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              } />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/profile" element={<Profile />} />
              <Route path="/profile/roadmaps" element={<Profile />} />
            </Routes>
          </main>
          <footer className="footer">
            <p>&copy; 2024 Your Project. All rights reserved.</p>
          </footer>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
}

export default App;
