import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Roadmap = () => {
  const [roadmap, setRoadmap] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRoadmap = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/mentor/my-plan', {
          withCredentials: true
        });
        lo
        if (response.data.success && response.data.plan) {
          setRoadmap(response.data.plan);
        } else {
          navigate('/create-mentor');
        }
      } catch (error) {
        setError('Failed to fetch your learning plan. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchRoadmap();
  }, [navigate]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading your personalized roadmap...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button 
          className="retry-button"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="roadmap-container">
      <div className="roadmap-header">
        <h1>Your Learning Roadmap</h1>
        <div className="roadmap-info">
          <p><strong>Career Goal:</strong> {roadmap?.careerGoal}</p>
          <p><strong>Technology:</strong> {roadmap?.technology}</p>
          <p><strong>Timeline:</strong> {roadmap?.timeline}</p>
          <p><strong>Schedule:</strong> {roadmap?.planPreference}</p>
        </div>
      </div>
      
      <div className="roadmap-content">
        {roadmap?.roadmap ? (
          <div className="roadmap-text">
            {roadmap.roadmap.split('\n').map((line, index) => (
              <p key={index}>{line}</p>
            ))}
          </div>
        ) : (
          <p className="error-message">No roadmap content available</p>
        )}
      </div>
    </div>
  );
};

export default Roadmap;
