import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Roadmap = () => {
  const [roadmap, setRoadmap] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRoadmap = async () => {
      try {
        console.log('Fetching roadmap...');
        const response = await axios.get('http://localhost:5000/api/mentor/my-plan', {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          }
        });
        
        console.log('Full API Response:', response.data);
        
        if (response.data.success && response.data.plan && response.data.plan.roadmap) {
          console.log('Roadmap content:', response.data.plan.roadmap);
          setRoadmap(response.data.plan);
        } else {
          console.error('Invalid plan data:', response.data);
          throw new Error('No valid roadmap found');
        }
      } catch (error) {
        console.error('Error fetching roadmap:', error);
        setError(error.response?.data?.message || error.message || 'Failed to fetch your learning plan');
      } finally {
        setLoading(false);
      }
    };

    fetchRoadmap();
  }, [navigate]);

  const formatContent = (content) => {
    if (!content) {
      console.log('No content to format');
      return null;
    }

    console.log('Formatting content:', content);

    return content.split('\n').map((line, index) => {
      const trimmedLine = line.trim();
      if (!trimmedLine) return <br key={index} />;

      // Handle headers
      if (trimmedLine.startsWith('#')) {
        const level = trimmedLine.match(/^#+/)[0].length;
        const text = trimmedLine.replace(/^#+\s*/, '');
        return React.createElement(`h${level}`, { 
          key: index, 
          className: 'roadmap-header',
          style: { color: 'var(--primary-color)' }
        }, text);
      }
      
      // Handle list items
      if (trimmedLine.startsWith('-')) {
        return (
          <li key={index} className="roadmap-list-item">
            {trimmedLine.substring(1).trim()}
          </li>
        );
      }

      // Handle normal text
      return <p key={index} className="roadmap-text-line">{trimmedLine}</p>;
    });
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loader"></div>
        <p>Loading your personalized roadmap...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button 
          className="retry-button"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  if (!roadmap || !roadmap.roadmap) {
    return (
      <div className="error-container">
        <p className="error-message">No roadmap found. Please create a new learning plan.</p>
        <button 
          className="retry-button"
          onClick={() => navigate('/create-mentor')}
        >
          Create New Plan
        </button>
      </div>
    );
  }

  return (
    <div className="roadmap-container">
      <div className="roadmap-header">
        <h1>Your Learning Roadmap</h1>
        <div className="roadmap-info">
          <p><strong>Career Goal:</strong> {roadmap.careerGoal}</p>
          <p><strong>Technology:</strong> {roadmap.technology}</p>
          <p><strong>Timeline:</strong> {roadmap.timeline}</p>
          <p><strong>Schedule:</strong> {roadmap.planPreference}</p>
        </div>
      </div>
      
      <div className="roadmap-content">
        <div className="roadmap-text">
          {formatContent(roadmap.roadmap)}
        </div>
      </div>

      {roadmap.parsedRoadmap?.milestones?.length > 0 && (
        <div className="milestones-section">
          <h2>Key Milestones</h2>
          <div className="milestones-grid">
            {roadmap.parsedRoadmap.milestones.map((milestone, index) => (
              <div key={index} className="milestone-card">
                <h3>{milestone.title}</h3>
                <p>Due: {new Date(milestone.dueDate).toLocaleDateString()}</p>
                <div className="milestone-status">
                  {milestone.completed ? 'Completed' : 'Pending'}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Roadmap;
