import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import NavBar from './components/NavBar';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import CreateMentor from './pages/CreateMentor';
import axios from 'axios';

import './styles/App.css';

function App() {
  const [isLoading, setIsLoading] = React.useState(false);
  const [careerRoadmap, setCareerRoadmap] = React.useState(null);
  const [selectedCareer, setSelectedCareer] = React.useState('technology');

  const generateCareerPlan = async () => {
    try {
      setIsLoading(true);
      
      const prompt = `Generate a detailed career roadmap for someone interested in ${selectedCareer || 'technology'}. 
      Include: 
      1. Required skills and certifications
      2. Timeline with milestones
      3. Learning resources
      4. Potential job roles at each stage`;

      const response = await axios.post('https://api.openai.com/v1/chat/completions', {
        model: 'gpt-3.5-turbo',
        messages: [{
          role: 'user',
          content: prompt
        }],
        temperature: 0.7,
        max_tokens: 1000
      }, {
        headers: {
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
          'Content-Type': 'application/json'
        }
      });

      const roadmap = response.data.choices[0].message.content;
      setCareerRoadmap(roadmap);
      
    } catch (error) {
      console.error('Error generating career plan:', error);
      alert('Failed to generate career plan. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Router>
      <div className="app">
        <NavBar />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/create-mentor" element={<CreateMentor />} />
          </Routes>
        </main>
        <footer className="footer">
          <p>&copy; 2024 Your Project. All rights reserved.</p>
        </footer>
        <button 
          onClick={generateCareerPlan}
          disabled={isLoading}
          className="create-plan-button"
        >
          {isLoading ? 'Generating Plan...' : 'Create Plan'}
        </button>

        {careerRoadmap && (
          <div className="roadmap-container">
            <h2>Your Career Roadmap</h2>
            <pre className="roadmap-content">
              {careerRoadmap}
            </pre>
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
