import React, { useState } from 'react';
import axios from 'axios';

const Home = () => {
  const [showPopup, setShowPopup] = useState(false);
  const [step, setStep] = useState(1);
  const [careerGoal, setCareerGoal] = useState('');
  const [technology, setTechnology] = useState('');
  const [roadmap, setRoadmap] = useState('');

  const handleNext = () => {
    if (step === 1 && careerGoal) {
      setStep(2);
    }
  };

  const handleConfirm = async () => {
    try {
      const res = await axios.post('http://localhost:5000/api/career/set-goal', {
        careerGoal,
        technology
      });
      setRoadmap(res.data.roadmap);
      setShowPopup(false);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <h1 className="text-4xl font-bold text-center mb-8">Welcome to Career Planner</h1>
      <div className="text-center">
        <button
          onClick={() => setShowPopup(true)}
          className="bg-blue-500 text-white px-6 py-3 rounded-lg"
        >
          Start Career Planning
        </button>
      </div>

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg w-96">
            {step === 1 ? (
              <>
                <h2 className="text-2xl mb-4">What is your career goal?</h2>
                <input
                  type="text"
                  value={careerGoal}
                  onChange={(e) => setCareerGoal(e.target.value)}
                  className="w-full p-2 border rounded mb-4"
                  placeholder="e.g., Full Stack Developer"
                />
                <button
                  onClick={handleNext}
                  className="float-right bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Next
                </button>
              </>
            ) : (
              <>
                <h2 className="text-2xl mb-4">Select the technology you want to learn</h2>
                <input
                  type="text"
                  value={technology}
                  onChange={(e) => setTechnology(e.target.value)}
                  className="w-full p-2 border rounded mb-4"
                  placeholder="e.g., MERN Stack"
                />
                <button
                  onClick={handleConfirm}
                  className="float-right bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Confirm
                </button>
              </>
            )}
          </div>
        </div>
      )}

      {roadmap && (
        <div className="mt-8 bg-white p-6 rounded-lg shadow">
          <h2 className="text-2xl font-bold mb-4">Your Career Roadmap</h2>
          <pre className="whitespace-pre-wrap">{roadmap}</pre>
        </div>
      )}
    </div>
  );
};

export default Home;
